// src/components/PostDetail.js
import React, { useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import Header from "./Header"; // HeaderÍ∞Ä components Ìè¥ÎçîÏóê ÏûàÎã§Í≥† Í∞ÄÏ†ï

const formatPostTime = (createdAt) => {
  if (!createdAt) return "";
  const d = new Date(createdAt);
  const year = d.getFullYear();
  const month = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  const hour = String(d.getHours()).toString().padStart(2, "0");
  const minute = String(d.getMinutes()).toString().padStart(2, "0");
  return `${year}. ${month}. ${day} ${hour}:${minute}`;
};

const formatCommentTime = (createdAt) => {
  const d = new Date(createdAt);
  const month = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  const hour = String(d.getHours()).toString().padStart(2, "0");
  const minute = String(d.getMinutes()).toString().padStart(2, "0");
  return `${month}/${day} ${hour}:${minute}`;
};

export default function PostDetail() {
  const navigate = useNavigate();
  const { state } = useLocation();   // PostItemÏóêÏÑú ÎÑòÍ∏¥ Îç∞Ïù¥ÌÑ∞
  const { id } = useParams();        // URLÏùò :id (ÏßÄÍ∏àÏùÄ Ïïà Ïç®ÎèÑ Îê®)

  const post = state?.post;

  // ÏÉàÎ°úÍ≥†Ïπ®Ìï¥ÏÑú stateÍ∞Ä ÏÇ¨ÎùºÏ°åÏùÑ Îïå ÎåÄÎπÑ
  if (!post) {
    return (
      <>
        <Header />
        <div className="w-full max-w-4xl mx-auto mt-24 px-3">
          <p className="text-center text-gray-500 py-10">
            Ïù¥ Í≤åÏãúÍ∏Ä Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§. (ÏÉàÎ°úÍ≥†Ïπ® ÎêòÍ±∞ÎÇò ÏßÅÏ†ë Ï£ºÏÜåÎ•º ÏûÖÎ†•ÌñàÏùÑ Ïàò ÏûàÏñ¥Ïöî)
          </p>
          <div className="flex justify-center">
            <button
              className="px-4 py-2 rounded-lg bg-green-600 text-white text-sm"
              onClick={() => navigate("/community")}
            >
              Ïª§ÎÆ§ÎãàÌã∞Î°ú ÎèåÏïÑÍ∞ÄÍ∏∞
            </button>
          </div>
        </div>
      </>
    );
  }

  // Ïù¥ ÌéòÏù¥ÏßÄ ÏïàÏóêÏÑú ÎåìÍ∏Ä ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [comments, setComments] = useState([]);
  const [commentInput, setCommentInput] = useState("");

  const handleAddComment = () => {
    if (!commentInput.trim()) return;
    const newComment = {
      id: Date.now(),
      content: commentInput.trim(),
      createdAt: new Date().toISOString(),
    };
    setComments((prev) => [...prev, newComment]);
    setCommentInput("");
  };

  return (
    <>
      <Header />

      <div className="w-full max-w-4xl mx-auto mt-24 px-3">
        <div className="bg-white rounded-xl shadow-md overflow-hidden border">
          {/* ÏÉÅÎã®: Í∏Ä Ï†ïÎ≥¥ */}
          <div className="px-8 pt-8 pb-6 border-b">
            <div className="flex items-center justify-between mb-3">
              <div className="flex items-center gap-2">
                <span className="text-xs px-2 py-1 rounded-full border border-green-500 text-green-700 font-semibold">
                  ÏßàÎ¨∏
                </span>
                <span className="text-xs px-2 py-1 rounded-full bg-green-100 text-green-700">
                  {post.category}
                </span>
              </div>
              <button
                className="text-xs px-3 py-1 rounded-full bg-green-100 text-green-700 hover:bg-green-200"
                onClick={() => navigate(-1)}
              >
                Îí§Î°úÍ∞ÄÍ∏∞
              </button>
            </div>

            <h2 className="text-2xl font-bold mb-2">{post.title}</h2>

            <div className="text-xs text-gray-400 flex items-center gap-2 mb-4">
              <span>{formatPostTime(post.createdAt)}</span>
              <span>¬∑</span>
              <span>Ï°∞Ìöå 0</span>
            </div>

            <p className="text-gray-800 text-sm leading-relaxed whitespace-pre-line">
              {post.content}
            </p>
          </div>

          {/* ÎåìÍ∏Ä ÏòÅÏó≠ */}
          <div className="px-8 pt-6 pb-8">
            <h3 className="font-semibold mb-3">ÎåìÍ∏Ä {comments.length}</h3>

            {/* ÎåìÍ∏Ä ÏûÖÎ†• */}
            <div className="border rounded-lg bg-gray-50 p-4 mb-6">
              <textarea
                className="w-full border rounded-lg p-2 h-20 resize-none text-sm bg-white focus:outline-none focus:ring-1 focus:ring-green-500"
                placeholder="ÎåìÍ∏ÄÏùÑ ÏûëÏÑ±Ìï¥Î≥¥ÏÑ∏Ïöî."
                value={commentInput}
                onChange={(e) => setCommentInput(e.target.value)}
              />
              <div className="flex justify-end mt-2">
                <button
                  className="px-4 py-2 rounded-lg bg-green-600 text-white text-sm hover:bg-green-700"
                  onClick={handleAddComment}
                >
                  Îì±Î°ù
                </button>
              </div>
            </div>

            {/* ÎåìÍ∏Ä Î¶¨Ïä§Ìä∏ */}
            <div className="space-y-3">
              {comments.length === 0 ? (
                <p className="text-sm text-gray-400">
                  ÏïÑÏßÅ Îì±Î°ùÎêú ÎåìÍ∏ÄÏù¥ ÏóÜÏäµÎãàÎã§.
                </p>
              ) : (
                comments.map((c) => (
                  <div
                    key={c.id}
                    className="border rounded-lg px-4 py-3 text-sm bg-white"
                  >
                    <div className="flex gap-3">
                      <div className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center text-xs text-gray-500 flex-shrink-0">
                        ÏùµÎ™Ö
                      </div>
                      <div className="flex-1">
                        <div className="flex items-center justify-between mb-1">
                          <div className="flex flex-col">
                            <span className="text-sm font-medium">ÏùµÎ™Ö</span>
                            <span className="text-xs text-gray-400">
                              {formatCommentTime(c.createdAt)}
                            </span>
                          </div>
                          <button className="flex items-center gap-1 text-xs text-gray-400 hover:text-green-600">
                            <span>üëç</span>
                            <span>0</span>
                          </button>
                        </div>
                        <p className="whitespace-pre-line text-gray-800 mt-1">
                          {c.content}
                        </p>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
