import React, { useState } from "react";
import axios from "axios";
import "./LoginModal.css";

const LoginModal = ({ onClose, onLoginSuccess }) => {
  const [formType, setFormType] = useState("login"); // login | signup | findId | findPassword
  const [formData, setFormData] = useState({
    userId: "",
    password: "",
    email: "",
    name: "",
    nickname: "",
  });
  const [resultMessage, setResultMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (formType === "login") {
        const res = await axios.post("http://localhost:5000/login", {
          userId: formData.userId,
          password: formData.password,
        });
        alert("로그인 성공!");
        onLoginSuccess(res.data);
        onClose();
      }

      else if (formType === "signup") {
        const res = await axios.post("http://localhost:5000/signup", {
          userId: formData.userId,
          password: formData.password,
          email: formData.email,
          name: formData.name,
          nickname: formData.nickname,
        });
        alert("회원가입 성공!");
        setFormType("login");
      }

      else if (formType === "findId") {
        const res = await axios.post("http://localhost:5000/find-id", {
          email: formData.email,
        });
        setResultMessage(`아이디는 "${res.data.userId}" 입니다.`);
      }

      else if (formType === "findPassword") {
        const res = await axios.post("http://localhost:5000/find-password", {
          userId: formData.userId,
          email: formData.email,
        });
        setResultMessage(`비밀번호는 "${res.data.password}" 입니다.`);
      }

    } catch (err) {
      console.error(err);
      alert("요청 처리 중 오류가 발생했습니다.");
    }
  };

  const resetForm = () => {
    setFormData({
      userId: "",
      password: "",
      email: "",
      name: "",
      nickname: "",
    });
    setResultMessage("");
  };

  const changeForm = (type) => {
    setFormType(type);
    resetForm();
  };

  return (
    <div className="modal-background">
      <div className="modal">
        <button className="close-button" onClick={onClose}>×</button>
        <h2>
          {formType === "login" && "로그인"}
          {formType === "signup" && "회원가입"}
          {formType === "findId" && "아이디 찾기"}
          {formType === "findPassword" && "비밀번호 찾기"}
        </h2>

        <form onSubmit={handleSubmit}>
          {(formType === "login" || formType === "signup" || formType === "findPassword") && (
            <input
              type="text"
              name="userId"
              placeholder="아이디"
              value={formData.userId}
              onChange={handleChange}
              required
            />
          )}

          {(formType === "login" || formType === "signup") && (
            <input
              type="password"
              name="password"
              placeholder="비밀번호"
              value={formData.password}
              onChange={handleChange}
              required
            />
          )}

          {(formType === "signup" || formType === "findId" || formType === "findPassword") && (
            <input
              type="email"
              name="email"
              placeholder="이메일"
              value={formData.email}
              onChange={handleChange}
              required
            />
          )}

          {formType === "signup" && (
            <>
              <input
                type="text"
                name="name"
                placeholder="이름"
                value={formData.name}
                onChange={handleChange}
                required
              />
              <input
                type="text"
                name="nickname"
                placeholder="닉네임"
                value={formData.nickname}
                onChange={handleChange}
                required
              />
            </>
          )}

          <button type="submit" className="submit-button">
            {formType === "login" && "로그인"}
            {formType === "signup" && "회원가입"}
            {formType === "findId" && "아이디 찾기"}
            {formType === "findPassword" && "비밀번호 찾기"}
          </button>
        </form>

        {resultMessage && <p className="result-message">{resultMessage}</p>}

        <div className="modal-footer">
          {formType !== "login" && (
            <button onClick={() => changeForm("login")}>로그인</button>
          )}
          {formType !== "signup" && (
            <button onClick={() => changeForm("signup")}>회원가입</button>
          )}
          {formType !== "findId" && (
            <button onClick={() => changeForm("findId")}>아이디 찾기</button>
          )}
          {formType !== "findPassword" && (
            <button onClick={() => changeForm("findPassword")}>비밀번호 찾기</button>
          )}
        </div>
      </div>
    </div>
  );
};

export default LoginModal;
