import React, { useEffect, useState } from "react";
import axios from "axios";

const MyPage = () => {
  const [userInfo, setUserInfo] = useState(null);
  const [formData, setFormData] = useState({});
  const [isEditing, setIsEditing] = useState(false);
  const [passwordData, setPasswordData] = useState({ current: "", new: "", confirm: "" });
  const [loading, setLoading] = useState(true);
  const [view, setView] = useState("info"); // info | password

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user || !user.userId) {
      alert("로그인이 필요합니다.");
      setLoading(false);
      return;
    }

    axios
      .get("http://localhost:5000/api/user/info", { params: { userId: user.userId } })
      .then(res => {
        setUserInfo(res.data);
        setFormData(res.data);
      })
      .catch(err => {
        console.error(err);
        alert("사용자 정보를 불러오지 못했습니다.");
      })
      .finally(() => setLoading(false));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordData(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = () => {
    axios.post("http://localhost:5000/api/user/update", formData)
      .then(() => {
        alert("정보 수정 완료!");
        setUserInfo(formData);
        setIsEditing(false);
      })
      .catch(err => alert("수정 실패: " + err.message));
  };

  const handlePasswordSave = () => {
    if (passwordData.new !== passwordData.confirm) {
      alert("새 비밀번호와 확인이 일치하지 않습니다.");
      return;
    }
    axios.post("http://localhost:5000/api/user/change-password", passwordData)
      .then(() => {
        alert("비밀번호 변경 완료!");
        setPasswordData({ current: "", new: "", confirm: "" });
      })
      .catch(err => alert("비밀번호 변경 실패: " + err.message));
  };

  if (loading) return <p>불러오는 중...</p>;
  if (!userInfo) return <p>사용자 정보를 불러오지 못했습니다.</p>;

  return (
    <div>
      <h2>마이페이지</h2>
      <div>
        <button onClick={() => setView("info")} disabled={view==="info"}>내 정보</button>
        <button onClick={() => setView("password")} disabled={view==="password"}>비밀번호 변경</button>
      </div>

      {view === "info" && (
        <div style={{ marginTop: "20px" }}>
          <p>아이디: {userInfo.userId}</p>
          <p>
            이름: {isEditing ? (
              <input name="name" value={formData.name} onChange={handleChange} />
            ) : userInfo.name}
          </p>
          <p>
            이메일: {isEditing ? (
              <input name="email" value={formData.email} onChange={handleChange} />
            ) : userInfo.email}
          </p>
          <div>
            {isEditing ? (
              <>
                <button onClick={handleSave}>저장</button>
                <button onClick={() => { setFormData(userInfo); setIsEditing(false); }}>취소</button>
              </>
            ) : (
              <button onClick={() => setIsEditing(true)}>수정하기</button>
            )}
          </div>
        </div>
      )}

      {view === "password" && (
        <div style={{ marginTop: "20px" }}>
          <p>
            현재 비밀번호: <input type="password" name="current" value={passwordData.current} onChange={handlePasswordChange} />
          </p>
          <p>
            새 비밀번호: <input type="password" name="new" value={passwordData.new} onChange={handlePasswordChange} />
          </p>
          <p>
            새 비밀번호 확인: <input type="password" name="confirm" value={passwordData.confirm} onChange={handlePasswordChange} />
          </p>
          <button onClick={handlePasswordSave}>비밀번호 변경</button>
        </div>
      )}
    </div>
  );
};

export default MyPage;
