import React, { useState } from 'react';
import './CategoryMenu.css';

const categories = [
  '전체','AI 개발','AI 활용(AX)','개발·프로그래밍','게임 개발',
  '데이터 사이언스','보안·네트워크','하드웨어','디자인·아트',
  '기획·경영·마케팅','업무 생산성','커리어·자기계발','대학 교육'
];

const categoryIcons = {
  '전체': '/img/all.png',
  'AI 개발': '/img/ai.png',
  'AI 활용(AX)': '/img/ax.png',
  '개발·프로그래밍': '/img/programming.png',
  '게임 개발': '/img/game.png',
  '데이터 사이언스': '/img/data.png',
  '보안·네트워크': '/img/security.png',
  '하드웨어': '/img/hardware.png',
  '디자인·아트': '/img/design.png',
  '기획·경영·마케팅': '/img/marketing.png',
  '업무 생산성': '/img/productivity.png',
  '커리어·자기계발': '/img/career.png',
  '대학 교육': '/img/education.png',
};

const CategoryMenu = () => {
  const [selectedCategory, setSelectedCategory] = useState('전체');

  return (
    <div className="category-menu">
      {categories.map(cat => (
        <button
          key={cat}
          className={`category-button ${selectedCategory === cat ? 'selected' : ''}`}
          onClick={() => setSelectedCategory(cat)}
        >
          <img src={categoryIcons[cat]} alt={cat} />
          <span>{cat}</span>
        </button>
      ))}
    </div>
  );
};

export default CategoryMenu;
