import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // ‚úÖ Ï∂îÍ∞Ä
import './Header.css';
import LoginModal from './LoginModal';

const Header = ({ isLoggedIn, setIsLoggedIn }) => {
  const [selected, setSelected] = useState('mentoring');
  const [showModal, setShowModal] = useState(false); 
  const navigate = useNavigate();

  // Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ Ïãú Ïã§Ìñâ
  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
    setShowModal(false);
  };

  // Î°úÍ∑∏ÏïÑÏõÉ Ïãú Ïã§Ìñâ
  const handleLogout = () => {
    setIsLoggedIn(false);
    alert("Î°úÍ∑∏ÏïÑÏõÉÎêòÏóàÏäµÎãàÎã§.");
  };

  return (
    <header className="header">
      <div className="header-top">

        {/* ÏôºÏ™Ω: Î°úÍ≥† */}
        <h1 className="logo">
          <img src="/img/logo.png" alt="Î°úÍ≥†" className="logo-img" />
        </h1>

        {/* Í∞ÄÏö¥Îç∞: Ïä§ÌÑ∞Îîî/Î©òÌÜ†ÎßÅ */}
        <div className="center-nav">
          <button
            className={`center-button ${selected === 'mentoring' ? 'active' : ''}`}
            onClick={() => setSelected('mentoring')}
          >
            <img src="/img/mentoring.png" alt="Î©òÌÜ†ÎßÅ" className="center-icon" />
            <span>Î©òÌÜ†ÎßÅ</span>
          </button>

          <button
            className={`center-button ${selected === 'study' ? 'active' : ''}`}
            onClick={() => setSelected('study')}
          >
            <img src="/img/study.png" alt="Ïä§ÌÑ∞Îîî" className="center-icon" />
            <span>Ïä§ÌÑ∞Îîî</span>
          </button>
        </div>

        {/* Ïò§Î•∏Ï™Ω: ÏÑ§Ï†ï + Î°úÍ∑∏Ïù∏/Î°úÍ∑∏ÏïÑÏõÉ */}
        <nav className="nav">
          
          {/* üî• Ïª§ÎÆ§ÎãàÌã∞ Ïù¥Îèô Î≤ÑÌäº (Ïó¨Í∏∞ ÏàòÏ†ïÎê®) */}
          <button 
            className="image-button" 
            onClick={() => navigate('/community')}
          >
            <img src="/img/setting.png" alt="ÏÑ§Ï†ï" className="button-image" />
          </button>

          {/* Î°úÍ∑∏Ïù∏ Ïó¨Î∂ÄÏóê Îî∞Î•∏ UI Î≥ÄÌôî */}
          {!isLoggedIn ? (
            <button className="login-button" onClick={() => setShowModal(true)}>
              Î°úÍ∑∏Ïù∏
            </button>
          ) : (
            <>
              <button 
                className="mypage-button" 
                onClick={() => navigate('/mypage')}
              >
                ÎÇ¥ Ï†ïÎ≥¥
              </button>

              <button className="logout-button" onClick={handleLogout}>
                Î°úÍ∑∏ÏïÑÏõÉ
              </button>
            </>
          )}
        </nav>
      </div>

      {/* Í≤ÄÏÉâÏ∞Ω */}
      <div className="search-container">
        <input type="text" className="search-input" placeholder="Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..." />
        <button className="search-button" onClick={() => alert("Í≤ÄÏÉâ!")}>
          <img src="/img/search.svg" alt="Í≤ÄÏÉâ" />
        </button>
      </div>

      {/* Î°úÍ∑∏Ïù∏ Î™®Îã¨ */}
      {showModal && (
        <LoginModal
          onClose={() => setShowModal(false)}
          onLoginSuccess={handleLoginSuccess}
        />
      )}
    </header>
  );
};

export default Header;
