import React, { useState } from "react";
import axios from "axios";
import "./LoginModal.css";

const LoginModal = ({ onClose, onLoginSuccess }) => {
  const [formType, setFormType] = useState("login");
  const [formData, setFormData] = useState({ userId: "", password: "", email: "" });
  const [resultMessage, setResultMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ✅ 로그인
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/login", formData);
      setResultMessage(res.data.message);
      if (res.data.success) {
        onLoginSuccess(res.data.user);
        onClose();
      }
    } catch {
      setResultMessage("서버 오류가 발생했습니다.");
    }
  };

  // ✅ 회원가입
  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/register", formData);
      setResultMessage(res.data.message);
      if (res.data.success) setFormType("login");
    } catch {
      setResultMessage("회원가입 중 오류가 발생했습니다.");
    }
  };

  // ✅ 아이디 찾기
  const handleFindId = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/find-id", { email: formData.email });
      if (res.data.success) {
        setResultMessage(`당신의 아이디는 "${res.data.userId}" 입니다.`);
      } else {
        setResultMessage(res.data.message);
      }
    } catch {
      setResultMessage("아이디 찾기 중 오류 발생");
    }
  };

  // ✅ 비밀번호 찾기
  const handleFindPassword = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/find-password", {
        userId: formData.userId,
        email: formData.email,
      });
      if (res.data.success) {
        setResultMessage(`비밀번호는 "${res.data.password}" 입니다.`);
      } else {
        setResultMessage(res.data.message);
      }
    } catch {
      setResultMessage("비밀번호 찾기 중 오류 발생");
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <button className="close-btn" onClick={onClose}>✕</button>

        {formType === "login" && (
          <form onSubmit={handleLogin}>
            <h2>로그인</h2>
            <input type="text" name="userId" placeholder="아이디" onChange={handleChange} required />
            <input type="password" name="password" placeholder="비밀번호" onChange={handleChange} required />
            <button type="submit">로그인</button>
            <p className="link" onClick={() => setFormType("register")}>회원가입</p>
            <p className="link" onClick={() => setFormType("find-id")}>아이디 찾기</p>
            <p className="link" onClick={() => setFormType("find-password")}>비밀번호 찾기</p>
          </form>
        )}

        {formType === "register" && (
          <form onSubmit={handleRegister}>
            <h2>회원가입</h2>
            <input type="text" name="userId" placeholder="아이디" onChange={handleChange} required />
            <input type="password" name="password" placeholder="비밀번호" onChange={handleChange} required />
            <input type="email" name="email" placeholder="이메일" onChange={handleChange} required />
            <button type="submit">회원가입</button>
            <p className="link" onClick={() => setFormType("login")}>로그인으로 돌아가기</p>
          </form>
        )}

        {formType === "find-id" && (
          <form onSubmit={handleFindId}>
            <h2>아이디 찾기</h2>
            <input type="email" name="email" placeholder="이메일 입력" onChange={handleChange} required />
            <button type="submit">아이디 찾기</button>
            <p className="link" onClick={() => setFormType("login")}>로그인으로 돌아가기</p>
          </form>
        )}

        {formType === "find-password" && (
          <form onSubmit={handleFindPassword}>
            <h2>비밀번호 찾기</h2>
            <input type="text" name="userId" placeholder="아이디" onChange={handleChange} required />
            <input type="email" name="email" placeholder="이메일" onChange={handleChange} required />
            <button type="submit">비밀번호 찾기</button>
            <p className="link" onClick={() => setFormType("login")}>로그인으로 돌아가기</p>
          </form>
        )}

        {resultMessage && <p className="result-message">{resultMessage}</p>}
      </div>
    </div>
  );
};

export default LoginModal;
