import React, { useState } from "react";
import axios from "axios";
import "./LoginModal.css";

const LoginModal = ({ onClose, onLoginSuccess }) => {
  const [formType, setFormType] = useState("login"); // login | signup | findId | findPassword
  const [formData, setFormData] = useState({ userId: "", password: "", email: "" });
  const [resultMessage, setResultMessage] = useState(""); // ÏÑúÎ≤Ñ ÏùëÎãµ Î©îÏãúÏßÄ ÌëúÏãúÏö©

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setResultMessage(""); // Ï†úÏ∂ú Ïãú Í≤∞Í≥º Ï¥àÍ∏∞Ìôî

    try {
      let url = "";
      let requestData = {};

      if (formType === "login") {
        url = "http://localhost:5000/login";
        requestData = { userId: formData.userId, password: formData.password };
      } else if (formType === "signup") {
        url = "http://localhost:5000/signup";
        requestData = { userId: formData.userId, password: formData.password, email: formData.email };
      } else if (formType === "findId") {
        url = "http://localhost:5000/find-id";
        requestData = { email: formData.email };
      } else if (formType === "findPassword") {
        url = "http://localhost:5000/find-password";
        requestData = { userId: formData.userId, email: formData.email };
      }

      const response = await axios.post(url, requestData);
      const data = response.data;

      if (data.success) {
        if (formType === "login") {
          setResultMessage("‚úÖ Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!");
          if (onLoginSuccess) onLoginSuccess();
          setTimeout(() => onClose(), 1200);
        } else if (formType === "signup") {
          setResultMessage("‚úÖ ÌöåÏõêÍ∞ÄÏûÖ ÏôÑÎ£å! Î°úÍ∑∏Ïù∏ Ìï¥Ï£ºÏÑ∏Ïöî.");
          setFormType("login");
        } else if (formType === "findId") {
          setResultMessage(`üîç ÏïÑÏù¥Îîî: ${data.userId}`);
        } else if (formType === "findPassword") {
          setResultMessage(`üîê ÎπÑÎ∞ÄÎ≤àÌò∏: ${data.password}`);
        }
      } else {
        setResultMessage(`‚ùå ${data.message}`);
      }
    } catch (err) {
      console.error(err);
      setResultMessage("‚ö†Ô∏è ÏÑúÎ≤Ñ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  };

  const switchForm = (type) => {
    setFormType(type);
    setResultMessage(""); // Ìèº Ï†ÑÌôò Ïãú Î©îÏãúÏßÄ Ï¥àÍ∏∞Ìôî
    setFormData({ userId: "", password: "", email: "" }); // ÏûÖÎ†•Í∞í Ï¥àÍ∏∞Ìôî
  };

  const renderForm = () => {
    switch (formType) {
      case "login":
        return (
          <>
            <form onSubmit={handleSubmit} className="login-form">
              <input type="text" name="userId" placeholder="ÏïÑÏù¥Îîî" value={formData.userId} onChange={handleChange} required />
              <input type="password" name="password" placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏" value={formData.password} onChange={handleChange} required />
              <button type="submit" className="login-btn">Î°úÍ∑∏Ïù∏</button>
            </form>
            <div className="login-links">
              <button className="link-btn" onClick={() => switchForm("signup")}>ÌöåÏõêÍ∞ÄÏûÖ</button>
              <button className="link-btn" onClick={() => switchForm("findId")}>ÏïÑÏù¥Îîî Ï∞æÍ∏∞</button>
              <button className="link-btn" onClick={() => switchForm("findPassword")}>ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞</button>
            </div>
          </>
        );

      case "signup":
        return (
          <>
            <form onSubmit={handleSubmit} className="login-form">
              <input type="text" name="userId" placeholder="ÏïÑÏù¥Îîî" value={formData.userId} onChange={handleChange} required />
              <input type="password" name="password" placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏" value={formData.password} onChange={handleChange} required />
              <input type="email" name="email" placeholder="Ïù¥Î©îÏùº" value={formData.email} onChange={handleChange} required />
              <button type="submit" className="login-btn">ÌöåÏõêÍ∞ÄÏûÖ</button>
            </form>
            <button className="link-btn" onClick={() => switchForm("login")}>Î°úÍ∑∏Ïù∏ ÌôîÎ©¥ÏúºÎ°ú</button>
          </>
        );

      case "findId":
        return (
          <>
            <form onSubmit={handleSubmit} className="login-form">
              <input type="email" name="email" placeholder="Í∞ÄÏûÖÌïú Ïù¥Î©îÏùº ÏûÖÎ†•" value={formData.email} onChange={handleChange} required />
              <button type="submit" className="login-btn">ÏïÑÏù¥Îîî Ï∞æÍ∏∞</button>
            </form>
            <button className="link-btn" onClick={() => switchForm("login")}>Î°úÍ∑∏Ïù∏ ÌôîÎ©¥ÏúºÎ°ú</button>
          </>
        );

      case "findPassword":
        return (
          <>
            <form onSubmit={handleSubmit} className="login-form">
              <input type="text" name="userId" placeholder="ÏïÑÏù¥Îîî ÏûÖÎ†•" value={formData.userId} onChange={handleChange} required />
              <input type="email" name="email" placeholder="Í∞ÄÏûÖÌïú Ïù¥Î©îÏùº ÏûÖÎ†•" value={formData.email} onChange={handleChange} required />
              <button type="submit" className="login-btn">ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞</button>
            </form>
            <button className="link-btn" onClick={() => switchForm("login")}>Î°úÍ∑∏Ïù∏ ÌôîÎ©¥ÏúºÎ°ú</button>
          </>
        );

      default:
        return null;
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-container" onClick={(e) => e.stopPropagation()}>
        <img src="/img/logo.png" alt="Î°úÍ≥†" className="modal-logo" />
        {/* Î©îÏãúÏßÄÎ•º Î°úÍ≥† Î∞ë, Ìèº ÏúÑÏóê ÌëúÏãú */}
        <p
          className={`result-message ${resultMessage.includes("‚úÖ") ? "success" : resultMessage.includes("‚ùå") || resultMessage.includes("‚ö†Ô∏è") ? "error" : ""}`}
        >
          {resultMessage}
        </p>
        {renderForm()}
      </div>
    </div>
  );
};

export default LoginModal;
