import React, { useEffect, useState } from "react";
import axios from "axios";

const MyPage = () => {
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    // 로그인한 사용자 정보 가져오기
    axios
      .get("http://localhost:5000/api/user/info", { withCredentials: true })
      .then((res) => {
        if (res.data.success) {
          setUserInfo(res.data.user);
        } else {
          setError("로그인이 필요합니다.");
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error("❌ 사용자 정보 요청 오류:", err);
        setError("사용자 정보를 불러오지 못했습니다.");
        setLoading(false);
      });
  }, []);

  if (loading) return <div style={{ textAlign: "center", marginTop: "50px" }}>⏳ 로딩중...</div>;
  if (error) return <div style={{ textAlign: "center", marginTop: "50px", color: "red" }}>{error}</div>;

  return (
    <div style={{ maxWidth: "600px", margin: "50px auto", textAlign: "center" }}>
      <h2>내 정보</h2>
      <div style={{ marginTop: "30px", textAlign: "left", border: "1px solid #ddd", padding: "20px", borderRadius: "8px" }}>
        <p><strong>아이디:</strong> {userInfo.userId}</p>
        <p><strong>이름:</strong> {userInfo.name || "입력되지 않음"}</p>
        <p><strong>닉네임:</strong> {userInfo.nickname || "입력되지 않음"}</p>
        <p><strong>이메일:</strong> {userInfo.email}</p>
        <p><strong>가입일:</strong> {new Date(userInfo.join_date).toLocaleDateString()}</p>
      </div>

      <button
        onClick={() => {
          axios.post("http://localhost:5000/logout", {}, { withCredentials: true })
            .then(() => window.location.href = "/");
        }}
        style={{
          marginTop: "20px",
          backgroundColor: "#f44336",
          color: "white",
          border: "none",
          padding: "10px 20px",
          borderRadius: "5px",
          cursor: "pointer"
        }}
      >
        로그아웃
      </button>
    </div>
  );
};

export default MyPage;
