import React, { useEffect, useState } from "react";
import axios from "axios";
import "./MyPage.css"; // ✅ CSS 연동

const MyPage = () => {
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");
    if (!user || !token) {
      alert("로그인이 필요합니다.");
      setLoading(false);
      return;
    }

    axios
      .post(
        "http://localhost:5000/api/user-info",
        { userId: user.userId },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then((res) => setUserInfo(res.data))
      .catch((err) => {
        console.error("❌ 사용자 정보 불러오기 실패:", err.response || err);
        alert("사용자 정보를 불러오지 못했습니다.");
      })
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <p className="loading">불러오는 중...</p>;
  if (!userInfo) return <p className="error">사용자 정보를 불러오지 못했습니다.</p>;

  return (
    <div className="mypage-container">
      <div className="mypage-card">
        <h2 className="mypage-title">마이페이지</h2>
        <div className="mypage-info">
          <p><span>아이디</span> {userInfo.userId}</p>
          <p><span>이름</span> {userInfo.name || "미등록"}</p>
          <p><span>닉네임</span> {userInfo.nickname || "미등록"}</p>
          <p><span>이메일</span> {userInfo.email}</p>
          <p><span>가입일</span> {new Date(userInfo.join_date).toLocaleDateString()}</p>
        </div>
      </div>
    </div>
  );
};

export default MyPage;
