import React, { useState } from "react";
import axios from "axios";
import "./LoginModal.css";

const LoginModal = ({ onClose, onLoginSuccess }) => {
  const [formType, setFormType] = useState("login"); // login | signup | findId | findPassword
  const [formData, setFormData] = useState({
    userId: "",
    password: "",
    email: "",
    name: "",
    nickname: "",
  });
  const [resultMessage, setResultMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (formType === "login") {
        const res = await axios.post("http://localhost:5000/login", {
          userId: formData.userId,
          password: formData.password,
        });
        alert("로그인 성공!");
        onLoginSuccess(res.data.user);
        onClose();
      } 
      else if (formType === "signup") {
        const res = await axios.post("http://localhost:5000/signup", formData);
        setResultMessage(res.data.message);
      } 
      else if (formType === "findId") {
        const res = await axios.post("http://localhost:5000/find-id", {
          email: formData.email,
        });
        setResultMessage(`아이디: ${res.data.userId}`);
      } 
      else if (formType === "findPassword") {
        const res = await axios.post("http://localhost:5000/find-password", {
          userId: formData.userId,
          email: formData.email,
        });
        setResultMessage(`비밀번호: ${res.data.password}`);
      }
    } catch (err) {
      setResultMessage(err.response?.data?.message || "오류가 발생했습니다.");
    }
  };

  const changeForm = (type) => {
    setFormType(type);
    setResultMessage("");
    setFormData({
      userId: "",
      password: "",
      email: "",
      name: "",
      nickname: "",
    });
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <button className="close-btn" onClick={onClose}>X</button>

        {formType === "login" && (
          <>
            <h2>로그인</h2>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                name="userId"
                placeholder="아이디"
                value={formData.userId}
                onChange={handleChange}
                required
              />
              <input
                type="password"
                name="password"
                placeholder="비밀번호"
                value={formData.password}
                onChange={handleChange}
                required
              />
              <button type="submit">로그인</button>
            </form>
            <p>{resultMessage}</p>
            <div className="link-box">
              <button onClick={() => changeForm("signup")}>회원가입</button>
              <button onClick={() => changeForm("findId")}>아이디 찾기</button>
              <button onClick={() => changeForm("findPassword")}>비밀번호 찾기</button>
            </div>
          </>
        )}

        {formType === "signup" && (
          <>
            <h2>회원가입</h2>
            <form onSubmit={handleSubmit}>
              <input type="text" name="userId" placeholder="아이디" value={formData.userId} onChange={handleChange} required />
              <input type="password" name="password" placeholder="비밀번호" value={formData.password} onChange={handleChange} required />
              <input type="email" name="email" placeholder="이메일" value={formData.email} onChange={handleChange} required />
              <input type="text" name="name" placeholder="이름" value={formData.name} onChange={handleChange} required />
              <input type="text" name="nickname" placeholder="닉네임" value={formData.nickname} onChange={handleChange} required />
              <button type="submit">회원가입</button>
            </form>
            <p>{resultMessage}</p>
            <button onClick={() => changeForm("login")}>← 로그인으로 돌아가기</button>
          </>
        )}

        {formType === "findId" && (
          <>
            <h2>아이디 찾기</h2>
            <form onSubmit={handleSubmit}>
              <input
                type="email"
                name="email"
                placeholder="이메일 입력"
                value={formData.email}
                onChange={handleChange}
                required
              />
              <button type="submit">아이디 찾기</button>
            </form>
            <p>{resultMessage}</p>
            <button onClick={() => changeForm("login")}>← 로그인으로 돌아가기</button>
          </>
        )}

        {formType === "findPassword" && (
          <>
            <h2>비밀번호 찾기</h2>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                name="userId"
                placeholder="아이디 입력"
                value={formData.userId}
                onChange={handleChange}
                required
              />
              <input
                type="email"
                name="email"
                placeholder="이메일 입력"
                value={formData.email}
                onChange={handleChange}
                required
              />
              <button type="submit">비밀번호 찾기</button>
            </form>
            <p>{resultMessage}</p>
            <button onClick={() => changeForm("login")}>← 로그인으로 돌아가기</button>
          </>
        )}
      </div>
    </div>
  );
};

export default LoginModal;
