import React, { useState } from "react";
import CommunityCategory from "../../components/CommunityCategory";
import CommunityPostList from "../../components/CommunityPostList";
import CreatePostModal from "../../components/CreatePostModal";

const Community = () => {
  const [selectedCategory, setSelectedCategory] = useState("전체");
  const [posts, setPosts] = useState([]);
  const [openModal, setOpenModal] = useState(false);

  // 새 글 작성
  const handleCreate = (newPost) => {
    setPosts([{ id: Date.now(), ...newPost }, ...posts]);
  };

  // 필터링
  const filteredPosts =
    selectedCategory === "전체"
      ? posts
      : posts.filter((p) => p.category === selectedCategory);

  return (
    <div style={{ display: "flex", paddingTop: "120px" }}>

      {/* 글 작성 모달 */}
      {openModal && (
        <CreatePostModal 
          onClose={() => setOpenModal(false)}
          onCreate={handleCreate}
        />
      )}

      {/* 왼쪽 카테고리 */}
      <CommunityCategory 
        selected={selectedCategory} 
        onSelect={setSelectedCategory} 
      />

      {/* 오른쪽 게시글 & 글쓰기 버튼 */}
      <div style={{ flex: 1, padding: "20px 40px" }}>

        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <button
            onClick={() => setOpenModal(true)}
            style={{
              background: "#00a05c",
              color: "white",
              padding: "12px 18px",
              borderRadius: "24px",
              border: "none",
              fontSize: "15px",
              cursor: "pointer",
            }}
          >
            글 작성하기
          </button>
        </div>

        <CommunityPostList posts={filteredPosts} />
      </div>
    </div>
  );
};

export default Community;
