// src/components/PostDetail.js
import React, { useState } from "react";

const formatCommentTime = (createdAt) => {
  const d = new Date(createdAt);
  const month = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  const hour = String(d.getHours()).padStart(2, "0");
  const minute = String(d.getMinutes()).padStart(2, "0");
  return `${month}/${day} ${hour}:${minute}`;
};

export default function PostDetail({ post, comments, onAddComment, onClose }) {
  const [commentInput, setCommentInput] = useState("");

  const handleAddComment = () => {
    if (!commentInput.trim()) return;
    onAddComment(commentInput.trim());
    setCommentInput("");
  };

  return (
    <div className="fixed inset-0 bg-black/40 flex justify-center items-center z-50">
      <div className="bg-white w-full max-w-2xl p-6 rounded-xl shadow-lg max-h-[80vh] overflow-y-auto">
        {/* 게시글 내용 */}
        <div className="mb-4">
          <div className="flex items-center justify-between mb-1">
            <h2 className="text-2xl font-bold">{post.title}</h2>
            <span className="text-xs px-2 py-1 rounded-full bg-green-100 text-green-700">
              {post.category}
            </span>
          </div>
          <p className="mt-3 text-gray-700 whitespace-pre-line">
            {post.content}
          </p>
        </div>

        <hr className="my-4" />

        {/* 댓글 입력 */}
        <div className="mb-4">
          <h3 className="font-semibold mb-2">댓글</h3>
          <textarea
            className="w-full border rounded-lg p-2 h-20 resize-none"
            placeholder="댓글을 입력하세요..."
            value={commentInput}
            onChange={(e) => setCommentInput(e.target.value)}
          />
          <div className="flex justify-end mt-2">
            <button
              className="px-4 py-2 rounded-lg bg-green-600 text-white text-sm"
              onClick={handleAddComment}
            >
              등록
            </button>
          </div>
        </div>

        {/* 댓글 목록 */}
        <div className="space-y-2">
          {comments.length === 0 ? (
            <p className="text-sm text-gray-400">등록된 댓글이 없습니다.</p>
          ) : (
            comments.map((c) => (
              <div
                key={c.id}
                className="border rounded-lg px-3 py-2 text-sm bg-gray-50"
              >
                <div className="flex justify-between mb-1">
                  <span className="font-medium">익명</span>
                  <span className="text-xs text-gray-400">
                    {formatCommentTime(c.createdAt)}
                  </span>
                </div>
                <p className="whitespace-pre-line">{c.content}</p>
              </div>
            ))
          )}
        </div>

        {/* 닫기 버튼 */}
        <div className="flex justify-end mt-5">
          <button
            className="px-4 py-2 rounded-lg bg-gray-300 text-sm"
            onClick={onClose}
          >
            닫기
          </button>
        </div>
      </div>
    </div>
  );
}
