// src/pages/Community.js
import React, { useState } from "react";
import CategoryBar from "../components/CategoryBar";

export default function Community() {
  const [search, setSearch] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("전체");
  const [posts, setPosts] = useState([]);
  const [openModal, setOpenModal] = useState(false);

  const filteredPosts = posts.filter((post) => {
    const matchCategory =
      selectedCategory === "전체" || post.category === selectedCategory;
    const matchSearch =
      post.title.includes(search) || post.content.includes(search);
    return matchCategory && matchSearch;
  });

  const addPost = (post) => {
    setPosts([...posts, post]);
  };

  return (
    <div className="w-full max-w-5xl mx-auto mt-10 px-3">
      {/* 검색창 */}
      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="검색어를 입력하세요..."
          className="w-full max-w-xl px-4 py-3 border rounded-full shadow-sm focus:ring-2 focus:ring-green-500 outline-none"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      {/* 카테고리 */}
      <CategoryBar
        selected={selectedCategory}
        setSelected={setSelectedCategory}
      />

      {/* 게시글 리스트 */}
      <PostList posts={filteredPosts} />

      {/* 글쓰기 버튼 */}
      <button
        onClick={() => setOpenModal(true)}
        className="fixed bottom-10 right-10 bg-green-600 text-white px-5 py-3 rounded-full shadow-lg hover:bg-green-700 transition"
      >
        글 작성하기 +
      </button>

      {openModal && (
        <PostModal onClose={() => setOpenModal(false)} onSubmit={addPost} />
      )}
    </div>
  );
}
