import React, { useState } from 'react';

function App() {
  const [page, setPage] = useState('login'); // login or register
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5000/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password, name, email })
    });
    const data = await response.json();
    setMessage(data.message);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5000/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password })
    });
    const data = await response.json();
    setMessage(data.success ? `환영합니다, ${data.user.name}님!` : data.message);
  };

  return (
    <div style={styles.container}>
      <h2>{page === 'login' ? '로그인' : '회원가입'}</h2>
      {page === 'login' ? (
        <form onSubmit={handleLogin} style={styles.form}>
          <input type="text" placeholder="아이디" value={username} onChange={e => setUsername(e.target.value)} style={styles.input} />
          <input type="password" placeholder="비밀번호" value={password} onChange={e => setPassword(e.target.value)} style={styles.input} />
          <button type="submit" style={styles.button}>로그인</button>
          <p onClick={() => setPage('register')} style={styles.link}>회원가입 하러가기</p>
        </form>
      ) : (
        <form onSubmit={handleRegister} style={styles.form}>
          <input type="text" placeholder="아이디" value={username} onChange={e => setUsername(e.target.value)} style={styles.input} />
          <input type="password" placeholder="비밀번호" value={password} onChange={e => setPassword(e.target.value)} style={styles.input} />
          <input type="text" placeholder="이름" value={name} onChange={e => setName(e.target.value)} style={styles.input} />
          <input type="email" placeholder="이메일" value={email} onChange={e => setEmail(e.target.value)} style={styles.input} />
          <button type="submit" style={styles.button}>회원가입</button>
          <p onClick={() => setPage('login')} style={styles.link}>로그인 하러가기</p>
        </form>
      )}
      <p>{message}</p>
    </div>
  );
}

const styles = {
  container: { width: '300px', margin: '100px auto', textAlign: 'center' },
  form: { display: 'flex', flexDirection: 'column', gap: '10px' },
  input: { padding: '10px', border: '1px solid #ccc', borderRadius: '5px' },
  button: { padding: '10px', backgroundColor: '#27ae60', color: 'white', border: 'none', borderRadius: '5px' },
  link: { color: 'blue', cursor: 'pointer', textDecoration: 'underline' }
};

export default App;
