// src/components/CategoryBar.js
import React from "react";

const categories = [
  "전체",
  "전자",
  "전기",
  "기계",
  "컴퓨터공학",
  "생명",
  "화학",
  "교육",
  "경영",
  "이공계",
  "법",
  "언어",
];

export default function CategoryBar({ selected, setSelected }) {
  return (
    // ✅ 세로로 쭉 내려가는 카테고리 바 (왼쪽)
    <div className="flex flex-col gap-2 w-28 mr-6">
      {categories.map((cat) => (
        <button
          key={cat}
          className={`px-3 py-2 rounded-lg text-sm text-left border transition
          ${
            selected === cat
              ? "bg-green-600 text-white border-green-600"
              : "bg-white border-gray-300 hover:bg-gray-100"
          }`}
          onClick={() => setSelected(cat)}
        >
          {cat}
        </button>
      ))}
    </div>
  );
}
