useEffect(() => {
  const user = JSON.parse(localStorage.getItem("user")); // 로그인 시 저장한 유저 정보
  if (!user) {
    alert("로그인이 필요합니다.");
    return;
  }

  axios.post("http://localhost:5000/api/user/info", { userId: user.userId })
    .then(res => {
      if (res.data.success) {
        setUserInfo(res.data.user);
        setFormData(res.data.user);
      } else {
        alert(res.data.message);
      }
    })
    .catch(err => {
      console.error("사용자 정보 불러오기 실패:", err);
      alert("사용자 정보를 불러오지 못했습니다.");
    });
}, []);
