import React, { useEffect, useState } from "react";
import axios from "axios";
import "./MyPage.css";

const MyPage = () => {
  const [userInfo, setUserInfo] = useState(null); // 서버에서 가져온 원본 정보
  const [isEditing, setIsEditing] = useState(false); // 수정 모드
  const [formData, setFormData] = useState({}); // 폼 입력값

  // 사용자 정보 불러오기
  useEffect(() => {
    axios
      .get("/api/user/info")
      .then((res) => {
        setUserInfo(res.data);
        setFormData(res.data); // 폼 초기화
      })
      .catch((err) => console.error("정보 불러오기 실패:", err));
  }, []);

  // input 변경 시 formData 업데이트
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // 정보 저장
  const handleSave = () => {
    axios
      .post("/api/user/update", formData)
      .then(() => {
        alert("수정 완료!");
        setUserInfo(formData); // 원본 정보 업데이트
        setIsEditing(false);
      })
      .catch((err) => alert("수정 실패: " + err.message));
  };

  // 수정 취소
  const handleCancel = () => {
    setFormData(userInfo); // 폼 데이터를 원본으로 되돌림
    setIsEditing(false);
  };

  if (!userInfo) return <div className="mypage-loading">로딩 중...</div>;

  return (
    <div className="mypage-container">
      <h1 className="mypage-title">내 정보</h1>

      <div className="mypage-section">
        <div className="mypage-item">
          <label>아이디</label>
          <span>{userInfo.userId}</span>
        </div>

        <div className="mypage-item">
          <label>이름</label>
          {isEditing ? (
            <input name="name" value={formData.name} onChange={handleChange} />
          ) : (
            <span>{userInfo.name}</span>
          )}
        </div>

        <div className="mypage-item">
          <label>이메일</label>
          {isEditing ? (
            <input name="email" value={formData.email} onChange={handleChange} />
          ) : (
            <span>{userInfo.email}</span>
          )}
        </div>

        <div className="mypage-item">
          <label>닉네임</label>
          {isEditing ? (
            <input name="nickname" value={formData.nickname} onChange={handleChange} />
          ) : (
            <span>{userInfo.nickname}</span>
          )}
        </div>

        <div className="mypage-item">
          <label>가입일</label>
          <span>{userInfo.joinDate}</span>
        </div>
      </div>

      <div className="mypage-btn-area">
        {isEditing ? (
          <>
            <button className="save-btn" onClick={handleSave}>
              저장
            </button>
            <button className="cancel-btn" onClick={handleCancel}>
              취소
            </button>
          </>
        ) : (
          <button className="edit-btn" onClick={() => setIsEditing(true)}>
            수정하기
          </button>
        )}
      </div>
    </div>
  );
};

export default MyPage;
