import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import Header from './components/Header';
import CategoryMenu from './components/CategoryMenu';
import Banner from './components/Banner';
import MentorSection from './components/MentorSection';
import Home from './pages/Home';
import MyPage from './pages/MyPage';

axios.defaults.withCredentials = true; // ✅ 세션 쿠키 포함 설정

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true); // ✅ 로그인 상태 확인 중 표시

  // ✅ 페이지가 새로고침될 때 서버 세션으로 로그인 상태 확인
  useEffect(() => {
    axios
      .get('http://localhost:5000/api/user/info')
      .then((res) => {
        if (res.data.success) {
          setIsLoggedIn(true);
        } else {
          setIsLoggedIn(false);
        }
      })
      .catch(() => setIsLoggedIn(false))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div>로딩 중...</div>; // ✅ 초기 상태 표시

  return (
    <Router>
      <Header isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} />
      <CategoryMenu />

      <Routes>
        <Route path="/" element={<Home />} />
        <Route
          path="/mypage"
          element={isLoggedIn ? <MyPage /> : <Navigate to="/" replace />}
        />
      </Routes>

      <Banner />
      <MentorSection />
    </Router>
  );
}

export default App;
