import React, { useEffect, useState } from "react";
import axios from "axios";
import "./MyPage.css";

const MyPage = () => {
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeSection, setActiveSection] = useState("info");
  const [myPosts, setMyPosts] = useState([]);
  const [myMentoring, setMyMentoring] = useState([]);
  const [myStudy, setMyStudy] = useState([]);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");

    if (!user || !token) {
      alert("로그인이 필요합니다.");
      setLoading(false);
      return;
    }

    // ✅ 사용자 정보 불러오기
    axios
      .post(
        "http://localhost:5000/api/user-info",
        { userId: user.userId },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then((res) => setUserInfo(res.data))
      .catch((err) => {
        console.error("❌ 사용자 정보 불러오기 실패:", err.response || err);
        alert("사용자 정보를 불러오지 못했습니다.");
      })
      .finally(() => setLoading(false));

    // ✅ 내가 쓴 글, 멘토링, 스터디 내역 불러오기 (예시)
    axios
      .get(`http://localhost:5000/api/my-posts?userId=${user.userId}`)
      .then((res) => setMyPosts(res.data))
      .catch(() => setMyPosts([]));

    axios
      .get(`http://localhost:5000/api/my-mentoring?userId=${user.userId}`)
      .then((res) => setMyMentoring(res.data))
      .catch(() => setMyMentoring([]));

    axios
      .get(`http://localhost:5000/api/my-study?userId=${user.userId}`)
      .then((res) => setMyStudy(res.data))
      .catch(() => setMyStudy([]));
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    alert("로그아웃 되었습니다.");
    window.location.href = "/login";
  };

  if (loading) return <p>불러오는 중...</p>;
  if (!userInfo) return <p>사용자 정보를 불러오지 못했습니다.</p>;

  return (
    <div className="mypage-container">
      {/* ✅ 사이드바 */}
      <div className="mypage-sidebar">
        <button
          className={activeSection === "info" ? "active" : ""}
          onClick={() => setActiveSection("info")}
        >
          내 정보
        </button>
        <button
          className={activeSection === "edit" ? "active" : ""}
          onClick={() => setActiveSection("edit")}
        >
          정보 수정
        </button>
        <button
          className={activeSection === "password" ? "active" : ""}
          onClick={() => setActiveSection("password")}
        >
          비밀번호 변경
        </button>
        <button
          className={activeSection === "mentoring" ? "active" : ""}
          onClick={() => setActiveSection("mentoring")}
        >
          내 멘토링 내역
        </button>
        <button
          className={activeSection === "study" ? "active" : ""}
          onClick={() => setActiveSection("study")}
        >
          내 스터디 내역
        </button>
        <button
          className={activeSection === "posts" ? "active" : ""}
          onClick={() => setActiveSection("posts")}
        >
          내가 쓴 글
        </button>
        <button
          className={activeSection === "settings" ? "active" : ""}
          onClick={() => setActiveSection("settings")}
        >
          환경 설정
        </button>
        <button
          className={activeSection === "delete" ? "active" : ""}
          onClick={() => setActiveSection("delete")}
        >
          회원 탈퇴
        </button>
        <button onClick={handleLogout}>로그아웃</button>
      </div>

      {/* ✅ 오른쪽 콘텐츠 */}
      <div className="mypage-content">
        {/* 내 정보 */}
        {activeSection === "info" && (
          <section>
            <h2>내 정보</h2>
            <div className="info-item">
              <label>아이디</label>
              <p>{userInfo.userId}</p>
            </div>
            <div className="info-item">
              <label>이름</label>
              <p>{userInfo.name || "미등록"}</p>
            </div>
            <div className="info-item">
              <label>닉네임</label>
              <p>{userInfo.nickname || "미등록"}</p>
            </div>
            <div className="info-item">
              <label>이메일</label>
              <p>{userInfo.email}</p>
            </div>
            <div className="info-item">
              <label>회원 등급</label>
              <p>{userInfo.role === "mentor" ? "멘토" : "일반회원"}</p>
            </div>
            <div className="info-item">
              <label>가입일</label>
              <p>{new Date(userInfo.join_date).toLocaleDateString()}</p>
            </div>
          </section>
        )}

        {/* 정보 수정 */}
        {activeSection === "edit" && (
          <section>
            <h2>정보 수정</h2>
            <div className="info-item">
              <label>닉네임</label>
              <input type="text" defaultValue={userInfo.nickname || ""} />
            </div>
            <div className="info-item">
              <label>이메일</label>
              <input type="email" defaultValue={userInfo.email || ""} />
            </div>
            <div className="btn-group">
              <button style={{ backgroundColor: "#4caf50", color: "#fff" }}>저장</button>
              <button style={{ backgroundColor: "#ccc" }}>취소</button>
            </div>
          </section>
        )}

        {/* 비밀번호 변경 */}
        {activeSection === "password" && (
          <section>
            <h2>비밀번호 변경</h2>
            <div className="info-item">
              <label>현재 비밀번호</label>
              <input type="password" />
            </div>
            <div className="info-item">
              <label>새 비밀번호</label>
              <input type="password" />
            </div>
            <div className="info-item">
              <label>새 비밀번호 확인</label>
              <input type="password" />
            </div>
            <div className="btn-group">
              <button style={{ backgroundColor: "#4caf50", color: "#fff" }}>변경</button>
              <button style={{ backgroundColor: "#ccc" }}>취소</button>
            </div>
          </section>
        )}

        {/* 내 멘토링 내역 */}
        {activeSection === "mentoring" && (
          <section>
            <h2>내 멘토링 내역</h2>
            {myMentoring.length === 0 ? (
              <p>멘토링 내역이 없습니다.</p>
            ) : (
              <ul>
                {myMentoring.map((item) => (
                  <li key={item.id}>
                    <strong>{item.title}</strong> — {item.status}
                    <br />
                    <small>{new Date(item.date).toLocaleDateString()}</small>
                  </li>
                ))}
              </ul>
            )}
          </section>
        )}

        {/* 내 스터디 내역 */}
        {activeSection === "study" && (
          <section>
            <h2>내 스터디 내역</h2>
            {myStudy.length === 0 ? (
              <p>스터디 참여 내역이 없습니다.</p>
            ) : (
              <ul>
                {myStudy.map((study) => (
                  <li key={study.id}>
                    <strong>{study.name}</strong> — {study.role}
                    <br />
                    <small>{new Date(study.joinedAt).toLocaleDateString()}</small>
                  </li>
                ))}
              </ul>
            )}
          </section>
        )}

        {/* 내가 쓴 글 */}
        {activeSection === "posts" && (
          <section>
            <h2>내가 쓴 글</h2>
            {myPosts.length === 0 ? (
              <p>작성한 게시글이 없습니다.</p>
            ) : (
              <ul>
                {myPosts.map((post) => (
                  <li key={post.id}>
                    <strong>{post.title}</strong> <br />
                    <small>{new Date(post.date).toLocaleDateString()}</small>
                  </li>
                ))}
              </ul>
            )}
          </section>
        )}

        {/* 환경 설정 */}
        {activeSection === "settings" && (
          <section>
            <h2>환경 설정</h2>
            <div className="info-item">
              <label>
                <input
                  type="checkbox"
                  onChange={() => document.body.classList.toggle("dark-mode")}
                />{" "}
                다크모드 전환
              </label>
            </div>
            <div className="info-item">
              <label>
                <input type="checkbox" /> 이메일 알림 수신
              </label>
            </div>
          </section>
        )}

        {/* 회원 탈퇴 */}
        {activeSection === "delete" && (
          <section>
            <h2>회원 탈퇴</h2>
            <p>정말로 탈퇴하시겠습니까? 이 작업은 되돌릴 수 없습니다.</p>
            <div className="info-item">
              <label>비밀번호 확인</label>
              <input type="password" />
            </div>
            <div className="btn-group">
              <button className="delete-btn">탈퇴하기</button>
              <button style={{ backgroundColor: "#ccc" }}>취소</button>
            </div>
          </section>
        )}
      </div>
    </div>
  );
};

export default MyPage;
