import React, { useEffect, useState } from "react";
import axios from "axios";
import "./MyPage.css"; // ✅ CSS 연결

const MyPage = () => {
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({ name: "", nickname: "", email: "" });

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");
    if (!user || !token) {
      alert("로그인이 필요합니다.");
      setLoading(false);
      return;
    }

    axios
      .post(
        "http://localhost:5000/api/user-info",
        { userId: user.userId },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then((res) => {
        setUserInfo(res.data);
        setFormData({
          name: res.data.name || "",
          nickname: res.data.nickname || "",
          email: res.data.email || "",
        });
      })
      .catch((err) => {
        console.error("❌ 사용자 정보 불러오기 실패:", err.response || err);
        alert("사용자 정보를 불러오지 못했습니다.");
      })
      .finally(() => setLoading(false));
  }, []);

  // ✅ 입력값 변경
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ✅ 정보 수정
  const handleUpdate = async () => {
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      const token = localStorage.getItem("token");

      await axios.post(
        "http://localhost:5000/api/update-user",
        { userId: user.userId, ...formData },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert("정보가 수정되었습니다!");

      // ✅ 최신 정보 다시 불러오기 (자동 새로고침 없이 반영)
      const res = await axios.post(
        "http://localhost:5000/api/user-info",
        { userId: user.userId },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setUserInfo(res.data);
      setEditing(false);
    } catch (err) {
      console.error(err);
      alert("정보 수정 중 오류가 발생했습니다.");
    }
  };

  // ✅ 회원탈퇴
  const handleDelete = async () => {
    if (!window.confirm("정말로 탈퇴하시겠습니까?")) return;
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      const token = localStorage.getItem("token");

      await axios.post(
        "http://localhost:5000/api/delete-user",
        { userId: user.userId },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert("회원 탈퇴가 완료되었습니다.");
      localStorage.clear();
      window.location.href = "/";
    } catch (err) {
      console.error(err);
      alert("회원 탈퇴 중 오류가 발생했습니다.");
    }
  };

  if (loading) return <p>불러오는 중...</p>;
  if (!userInfo) return <p>사용자 정보를 불러오지 못했습니다.</p>;

  return (
    <div className="mypage-container">
      <div className="mypage-sidebar">
        <button
          className={editing ? "" : "active"}
          onClick={() => setEditing(false)}
        >
          내 정보 보기
        </button>
        <button
          className={editing ? "active" : ""}
          onClick={() => setEditing(true)}
        >
          정보 수정
        </button>
        <button className="delete-btn" onClick={handleDelete}>
          회원 탈퇴
        </button>
      </div>

      <div className="mypage-content">
        {!editing ? (
          <>
            <h2>마이페이지</h2>
            <p>아이디: {userInfo.userId}</p>
            <p>이름: {userInfo.name || "미등록"}</p>
            <p>닉네임: {userInfo.nickname || "미등록"}</p>
            <p>이메일: {userInfo.email}</p>
            <p>가입일: {new Date(userInfo.join_date).toLocaleDateString()}</p>
          </>
        ) : (
          <>
            <h2>정보 수정</h2>
            <div className="info-item">
              <label>이름</label>
              <input
                name="name"
                value={formData.name}
                onChange={handleChange}
              />
            </div>
            <div className="info-item">
              <label>닉네임</label>
              <input
                name="nickname"
                value={formData.nickname}
                onChange={handleChange}
              />
            </div>
            <div className="info-item">
              <label>이메일</label>
              <input
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
            </div>
            <button onClick={handleUpdate}>저장</button>
          </>
        )}
      </div>
    </div>
  );
};

export default MyPage;
