import React, { useState } from "react";
import axios from "axios";
import "./LoginModal.css";

const LoginModal = ({ onClose, onLoginSuccess }) => {
  const [formType, setFormType] = useState("login"); // login | signup | findId | findPassword
  const [formData, setFormData] = useState({
    userId: "",
    password: "",
    email: "",
    name: "",
    nickname: "",
  });
  const [resultMessage, setResultMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setResultMessage("");

    try {
      let url = "";
      let requestData = {};

      switch (formType) {
        case "login":
          url = "http://localhost:5000/api/login";
          requestData = { userId: formData.userId, password: formData.password };
          break;

        case "signup":
          url = "http://localhost:5000/api/signup";
          requestData = {
            userId: formData.userId,
            password: formData.password,
            email: formData.email,
            name: formData.name,
            nickname: formData.nickname,
          };
          break;

        case "findId":
          url = "http://localhost:5000/api/find-id";
          requestData = { email: formData.email };
          break;

        case "findPassword":
          url = "http://localhost:5000/api/find-password";
          requestData = { userId: formData.userId, email: formData.email };
          break;

        default:
          return;
      }

      const { data } = await axios.post(url, requestData);

      if (data.success) {
        switch (formType) {
          case "login":
            localStorage.setItem("user", JSON.stringify(data.user));
            setResultMessage("‚úÖ Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!");
            if (onLoginSuccess) onLoginSuccess();
            setTimeout(() => onClose(), 1000);
            break;

          case "signup":
            setResultMessage("‚úÖ ÌöåÏõêÍ∞ÄÏûÖÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§! Î°úÍ∑∏Ïù∏ Ìï¥Ï£ºÏÑ∏Ïöî.");
            setFormType("login");
            break;

          case "findId":
            setResultMessage(`üîç ÏïÑÏù¥Îîî: ${data.userId}`);
            break;

          case "findPassword":
            setResultMessage(`üîê ÎπÑÎ∞ÄÎ≤àÌò∏: ${data.password}`);
            break;

          default:
            break;
        }
      } else {
        setResultMessage(`‚ùå ${data.message}`);
      }
    } catch (err) {
      console.error(err);
      setResultMessage("‚ö†Ô∏è ÏÑúÎ≤Ñ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  };

  const switchForm = (type) => {
    setFormType(type);
    setResultMessage("");
    setFormData({
      userId: "",
      password: "",
      email: "",
      name: "",
      nickname: "",
    });
  };

  const renderForm = () => {
    switch (formType) {
      case "login":
        return (
          <>
            <form onSubmit={handleSubmit} className="login-form">
              <input type="text" name="userId" placeholder="ÏïÑÏù¥Îîî" value={formData.userId} onChange={handleChange} required />
              <input type="password" name="password" placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏" value={formData.password} onChange={handleChange} required />
              <button type="submit" className="login-btn">Î°úÍ∑∏Ïù∏</button>
            </form>

            <div className="login-links">
              <button className="link-btn" onClick={() => switchForm("signup")}>ÌöåÏõêÍ∞ÄÏûÖ</button>
              <button className="link-btn" onClick={() => switchForm("findId")}>ÏïÑÏù¥Îîî Ï∞æÍ∏∞</button>
              <button className="link-btn" onClick={() => switchForm("findPassword")}>ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞</button>
            </div>
          </>
        );

      case "signup":
        return (
          <>
            <p className="form-title">ÌöåÏõêÍ∞ÄÏûÖ</p>
            <form onSubmit={handleSubmit} className="login-form signup-form">
              <input type="text" name="userId" placeholder="ÏïÑÏù¥Îîî" value={formData.userId} onChange={handleChange} required />
              <input type="password" name="password" placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏" value={formData.password} onChange={handleChange} required />
              <input type="text" name="name" placeholder="Ïù¥Î¶Ñ" value={formData.name} onChange={handleChange} required />
              <input type="text" name="nickname" placeholder="ÎãâÎÑ§ÏûÑ" value={formData.nickname} onChange={handleChange} required />
              <input type="email" name="email" placeholder="Ïù¥Î©îÏùº" value={formData.email} onChange={handleChange} required />
              <button type="submit" className="login-btn">ÌöåÏõêÍ∞ÄÏûÖ ÏôÑÎ£å</button>
            </form>
            <button className="back-btn" onClick={() => switchForm("login")}>Î°úÍ∑∏Ïù∏ ÌôîÎ©¥ÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</button>
          </>
        );

      case "findId":
        return (
          <>
            <p className="form-title">ÏïÑÏù¥Îîî Ï∞æÍ∏∞</p>
            <form onSubmit={handleSubmit} className="login-form">
              <input type="email" name="email" placeholder="Í∞ÄÏûÖÌïú Ïù¥Î©îÏùº ÏûÖÎ†•" value={formData.email} onChange={handleChange} required />
              <button type="submit" className="login-btn">ÏïÑÏù¥Îîî Ï∞æÍ∏∞</button>
            </form>
            <button className="back-btn" onClick={() => switchForm("login")}>Î°úÍ∑∏Ïù∏ ÌôîÎ©¥ÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</button>
          </>
        );

      case "findPassword":
        return (
          <>
            <p className="form-title">ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞</p>
            <form onSubmit={handleSubmit} className="login-form">
              <input type="text" name="userId" placeholder="ÏïÑÏù¥Îîî ÏûÖÎ†•" value={formData.userId} onChange={handleChange} required />
              <input type="email" name="email" placeholder="Í∞ÄÏûÖÌïú Ïù¥Î©îÏùº ÏûÖÎ†•" value={formData.email} onChange={handleChange} required />
              <button type="submit" className="login-btn">ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞</button>
            </form>
            <button className="back-btn" onClick={() => switchForm("login")}>Î°úÍ∑∏Ïù∏ ÌôîÎ©¥ÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</button>
          </>
        );

      default:
        return null;
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-container" onClick={(e) => e.stopPropagation()}>
        <img src="/img/logo.png" alt="Î°úÍ≥†" className="modal-logo" />
        <p className={`result-message ${resultMessage.includes("‚úÖ") ? "success" : resultMessage.includes("‚ùå") || resultMessage.includes("‚ö†Ô∏è") ? "error" : ""}`}>
          {resultMessage}
        </p>
        {renderForm()}
      </div>
    </div>
  );
};

export default LoginModal;
